import {
  Box,
  Divider,
  Heading,
  Stack,
  Wrap,
  WrapItem,
  Text,
  Center,
  TagLeftIcon,
  Tag,
  TagLabel,
  Flex,
  pseudoSelectors,
} from "@chakra-ui/react";
import { CheckIcon } from "@chakra-ui/icons";
import Pic from "../pic/pic.component";

import { firestore, storage } from "../../firebase/firebase.config";
import { useEffect, useState } from "react";

function Pics(props) {
  const { title } = props;

  const [pics, setPics] = useState([]);

  useEffect(async () => {
    console.log("Fetching pics data from Firebase...");
    await firestore
      .collection("video")
      .get()
      .then((querySnapshot) => {
        querySnapshot.forEach(async (doc) => {
          const urlVideo = await storage
            .refFromURL(doc.data().video)
            .getDownloadURL();
          const thumbnail = await storage
            .refFromURL(doc.data().thumbnail)
            .getDownloadURL();

          setPics([
            {
              urlVideo: urlVideo,
              thumbnail: thumbnail,
            },
            ...pics,
          ]);
          console.log(pics);
        });

        //Aspettare le promises
        console.log("Terminat:", pics);
      })
      .catch((e) => {
        console.log("Error fetching pics", e);
      });
  }, []);

  return (
    <Stack p={4} spacing={6}>
      <Box>
        <Heading mb={4} size="lg">
          🤷🏼‍♂️ Ecco il creatore...
        </Heading>
        <Pic numero={1} title="Lorenzo Manoni (BigHands)" />
      </Box>
      <Box>
        <Heading size="lg">📆 Calendario</Heading>
        <Wrap pt={4} justify="center">
          <Pic numero={1} title="Lorenzo Manoni (BigHands)" />
          <Pic numero={1} title="Lorenzo Manoni (BigHands)" />
          <Pic numero={1} title="Lorenzo Manoni (BigHands)" />
        </Wrap>
      </Box>
    </Stack>
  );
}

export default Pics;
