import {
  Center,
  Container,
  Heading,
  Stack,
  Box,
  Button,
  Spinner,
  Text,
} from "@chakra-ui/react";

import { useState } from "react";

export default function SplashScreen({ handleClick }) {
  const [spinner, setSpinner] = useState(true);

  const close = async () => {
    setSpinner(false);
    setTimeout(() => {
      setSpinner(true);
      handleClick();
    }, 3000);
  };

  return (
    <Box>
      <Center h="90vh">
        <Box maxW="32rem" textAlign="center">
          <Heading mb={8}>Avvento dell'Elis ðŸŽ„</Heading>
          <Container>
            Benvenuti, ragazze e ragazzi. All'interno del calendario dell'avento
            dell'Elis.
          </Container>
          <Container>
            La geniale mente di @lorenzomanoni ha ideato questo format, dove
            ogni giorno viene pubblicato un nuovo video. Troverai riflessioni e
            importanti spunti di vita. Divertiti.
          </Container>
          <Button size="lg" colorScheme="green" mt="24px" onClick={close}>
            Entra
          </Button>
          <Box hidden={spinner}></Box>
        </Box>
      </Center>
      <Center>
        <Stack align="center">
          <Spinner hidden={spinner} color="red.500" />
          <Text>Powered by @matteomad</Text>
        </Stack>
      </Center>
    </Box>
  );
}
