import {
  Box,
  Divider,
  Heading,
  Stack,
  Wrap,
  WrapItem,
  Text,
  Center,
  TagLeftIcon,
  Tag,
  TagLabel,
  Flex,
  pseudoSelectors,
} from "@chakra-ui/react";
import { CheckIcon } from "@chakra-ui/icons";
import Pic from "../pic/pic.component";

import { firestore, storage } from "../../firebase/firebase.config";
import { useEffect, useState } from "react";

function Pics(props) {
  const { title } = props;

  const [pics, setPics] = useState([]);

  useEffect(() => {
    fetchPics();
  }, []);

  const fetchPics = async () => {
    const result = await firestore.collection("video").get();

    let docs = [];
    result.forEach((doc) => {
      docs.push(doc.data());
    });

    docs.forEach(async (doc) => {
      let videoUrl = storage.refFromURL(doc.video).getDownloadURL();
      let thumbUrl = storage.refFromURL(doc.thumbnail).getDownloadURL();

      Promise.all([videoUrl, thumbUrl], ([v, t]) => {
        setPics([
          {
            thumbnail: v,
            src: t,
          },
        ]);
      });
    });
    console.log(pics);
  };

  return (
    <Stack p={4} spacing={6}>
      <Box>
        <Heading mb={4} size="lg">
          🤷🏼‍♂️ Ecco il creatore...
        </Heading>
        <Pic numero={1} title="Lorenzo Manoni (BigHands)" />
      </Box>
      <Box>
        <Heading size="lg">📆 Calendario</Heading>
        <Wrap pt={4} justify="center">
          {pics.map((pic) => {
            return <Pic numero={pic.thumbnail} title={pic.src} />;
          })}
        </Wrap>
      </Box>
    </Stack>
  );
}

export default Pics;
