import { CheckIcon } from "@chakra-ui/icons";
import Pic from "../pic/pic.component";

import { firestore, storage } from "../../firebase/firebase.config";
import { useEffect, useState } from "react";

function Pics(props) {
  const { title } = props;

  var pics = [];

  useEffect(() => {
    fetchPics();
  }, []);

  const fetchPics = async () => {
    const result = await firestore.collection("video").get();

    let docs = [];
    result.forEach((doc) => {
      docs.push(doc.data());
    });

    docs.forEach(async (doc) => {
      let videoUrl = await storage.refFromURL(doc.video).getDownloadURL();
      let thumbUrl = await storage.refFromURL(doc.thumbnail).getDownloadURL();

