import { useState } from "react";

import SplashScreen from "../components/splash-screen/splash-screen.component";
import Home from "../components/home/home.component";

import { firestore, storage } from "../firebase/firebase.config";

import cookies from "next-cookies";

function HomePage({ splash }) {
  const [splashScreen, setSplashScreen] = useState(true);

  console.log("coo", splashScreen);
  const handleClose = () => {
    document.cookie = "splash=false";
    setSplashScreen(false);
  };

  return (
    <div>
      {splash}{" "}
      {splashScreen ? <SplashScreen handleClick={handleClose} /> : <Home />}
    </div>
  );
}

export default HomePage;

export async function getServerSideProps(context) {
  const cookieSplash = cookies(context).splash;

  return {
    props: {
      splash: cookieSplash,
    }, // will be passed to the page component as props
  };
}
