import {
  Center,
  Container,
  Heading,
  Stack,
  Box,
  Button,
  Spinner,
} from "@chakra-ui/react";

export default function SplashScreen({ handleClick }) {
  const spinnerVisible = true;

  const closeScreen = () => {
    setTimeout(() => {
      spinnerVisible = false;
    }, 2000);
    handleClick();
  };
  return (
    <Center h="100vh">
      <Box maxW="32rem" textAlign="center">
        <Heading mb={8}>Avvento dell'Elis ðŸŽ„</Heading>
        <Container>
          Benvenuti, ragazze e ragazzi. All'interno del calendario dell'avento
          dell'Elis.
        </Container>
        <Button size="lg" colorScheme="green" mt="24px" onClick={closeScreen}>
          Entra
        </Button>
        <Box hidden={spinnerVisible} p={8}>
          <Spinner color="red.500" />
        </Box>
      </Box>
    </Center>
  );
}
